      }
    }

		if ($con === null) {
			$con = Propel::getConnection(MaquinariaPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from Maquinaria object
		}

		if ($criteria->containsKey(MaquinariaPeer::MAQ_ID) && $criteria->keyContainsValue(MaquinariaPeer::MAQ_ID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.MaquinariaPeer::MAQ_ID.')');
		}


